#include <algorithm>
#include <iostream>
#include <utility>
#include <cstring>
#include <cassert>
#include <vector>
#include <random>
#include <cstdio>
#include <string>
#include <queue>
#include <tuple>
#define MOD 1000000007LL
typedef long long ll;
char str[22];
ll dp[202020];

int main() {
    int t;
	dp[0] = dp[1] = dp[2] = dp[3] = dp[4] = dp[5] = dp[6] = dp[7] = dp[8] = 2; dp[9] = 3; dp[10] = 4; dp[11] = 4; dp[12] = 4;
	for (int i = 13; i < 200001; ++i) {
		dp[i] = (dp[i - 9] + dp[i - 10]) % MOD;
	}
    for (scanf("%d", &t); t--; ) {
        int n = 0, m;
        ll res = 0;
        scanf("%s%d", str, &m);
        for (int i = 0; str[i]; ++i) {
            if ((int)(str[i] - 48) + m < 10) {
                res += 1;
            } else {
				res += dp[(m - (10 - (ll)(str[i] - 48)))];
                res %= MOD;
            }
        }
		res %= MOD;
        printf("%lld\n", res);
    }
    return 0;
}

